import React, { Component } from "react";
import {useForm} from 'react-hook-form'



export default function SignUp () {
    const {register,handleSubmit} = useForm()
    function onSubmit(data){
        console.log(data);
    }
    // render() {
        return (
            <form onSubmit={handleSubmit(onSubmit)}>
                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>Full name</label>
                    <input type="text" className="form-control" name="name" placeholder="Full name" inputRef={register}/>
                </div>

                <div className="form-group">
                    <label>phone</label>
                    <input type="number" name="phone" className="form-control" placeholder="Phone" inputRef={register}/>
                </div>

                <div className="form-group">
                    <label>Age</label>
                    <input type="number" name="Age"  className="form-control" placeholder="Enter age" inputRef={register}/>
                </div>
                <div className="form-group">
                    {/* <label>Is valid</label> */}

                    {/* <select name="verify" >
            <option value="volvo">true</option>
                <option value="saab">false</option>
                </select> */}
                </div>
                <div className="form-group">
                <select name="verify" >
            <option value="volvo">online</option>
                <option value="saab">offline</option>
                </select>
                </div>  

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                <p className="forgot-password text-right">
                    Already registered <a href="#">sign in?</a>
                </p>
            </form>
        );
    // }
}
